    static ArrayList<String> palavras = new ArrayList<>(List.of(lista));
    static HashSet<String> letrasNaoExistem = new HashSet<>();
    static HashMap<Integer,String> posicaoErrada = new HashMap<>();
    static HashMap<Integer,String> posicaoCerta = new HashMap<>();

    static String chooseWord(){
        Random random = new Random();
        return palavras.get(random.nextInt(palavras.size()));
    }

    static void getNaoExistem(String c, String g){
        for (int i = 0; i < c.length(); i++) {
            if (c.indexOf(g.charAt(i)) == -1){
                letrasNaoExistem.add(Character.toString(g.charAt(i)));
            }
        }
    }

    static void getPosicaoErrada(String c, String g){
        String c1 = c.substring(0,2);
        String c2 = c.substring(2,5);
        String g1 = g.substring(0,2);
        String g2 = g.substring(2,5);

        for (int i = 0; i < c1.length(); i++) {
            if(c1.indexOf(g1.charAt(i)) != -1 && c1.indexOf(g1.charAt(i)) != g1.indexOf(c1.charAt(i))){
                posicaoErrada.put(i,Character.toString(g1.charAt(i)));
            }
            if(c2.indexOf(g2.charAt(i)) != -1 && c2.indexOf(g2.charAt(i)) != g2.indexOf(c2.charAt(i))){
                posicaoErrada.put(i+2,Character.toString(g2.charAt(i)));
            }
        }

//        for (int i = 0; i < c.length(); i++) {
//            if(c.indexOf(g.charAt(i)) != -1 && c.indexOf(g.charAt(i)) != g.indexOf(c.charAt(i))){
//               posicaoErrada.put(i,Character.toString(g.charAt(i)));
//            }
//        }
    }

    static void getPosicaoCerta(String c,String g){
        for (int i = 0; i < c.length(); i++) {
            if(g.charAt(i) == c.charAt(i)){
                posicaoCerta.put(i,Character.toString(c.charAt(i)));
            }
        }
    }

    static void filterPalavras(){
        ArrayList<String> removidas = new ArrayList<>();
        String removida = "";

        for (String i: palavras) {
            for (int j = 0; j < i.length(); j++) {
                if(letrasNaoExistem.contains(Character.toString(i.charAt(j)))){
                    removida = i;
                }
                if(Character.toString(i.charAt(j)).equals(posicaoErrada.get(j))){
                    removida = i;
                }
                if(posicaoCerta.containsKey(j) && !posicaoCerta.get(j).equals(Character.toString(i.charAt(j)))){
                    removida = i;
                }
            }
            removidas.add(removida);
        }
        palavras.removeAll(removidas);
    }


    public static void main(String[] args) {
        String chosenWord = chooseWord();
        String guess = chooseWord();
        boolean sus = false;

        for (int i = 0; i < 5; i++) {
            System.out.println("Tentativa:" + (i + 1));
            if(chosenWord.equals(guess)){
                sus = true;
                break;
            }
            System.out.println("Resposta: "+ chosenWord);
            System.out.println("Chute: " + guess);

            getNaoExistem(chosenWord,guess);
            getPosicaoErrada(chosenWord,guess);
            getPosicaoCerta(chosenWord,guess);
            System.out.println(letrasNaoExistem.toString());
            System.out.println(posicaoErrada.toString());
            System.out.println(posicaoCerta.toString());

            filterPalavras();
            System.out.println(palavras);

            guess = chooseWord();
        }
        if(sus){
            System.out.println("Acertou! \nPalavra: " + chosenWord + " // " + palavras);
        }else {
            System.out.println("Falhou!");
        }


    }


